    const {transactions} = useContext(GlobalContext);
    const amounts = transactions.map(transaction=>transactions.amount);
    const total = amounts.reduce((acc,item)=>(acc+=item),0).tofi
    return (
        <>
        <h4>Your Balance</h4>
        <h1 id="balance">$0.00</h1>
        </>
    )
}
