import { Transaction } from "../Components/Transaction";

export default (state,action)=>{
    switch(action.type){
        // return state;

        case 'DELETE_TRANSACTION':{
            return {
                ...state,
                transactions:state.transactions.filter(transaction)
            }
        }
        default:
            return state;
    }
}
